class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>arr;
        for(auto it: colors) arr.push_back(it);
         for(auto it: colors) arr.push_back(it);
        int n=colors.size();
        int prev=0;
        int i=1;
        int ans=0;
        for(i=1;i<n-1+k;i++){
           if(arr[i]!=arr[i-1]) continue;
            ans+=max(i-prev-k+1,0);
            prev=i;
        }
        ans+=max(i-prev-k+1,0);
                     return ans;

    }
};
