class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<n;i++) colors.push_back(colors[i]);
        int original_n = n;
        n = colors.size();

        int l = 0;
        int ans = 0;
        int r = 1;
        int count = 1;
        while(r < n) {
            if(colors[r] != colors[r - 1]) {
                count++;
                if(count == k) {
                    ans++;
                    count--;
                    l++;
                }
            } else {
                l = r;
                count = 1;
            }
            r++;
            if(l >= original_n) break;
        }
        return ans;
    }
};
