class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        // 0 1 0 1 0 | 0 1 0 1 0
        vector<int>temp=colors;
        int ans=0;
        for(auto it:colors)
        {
            temp.push_back(it);
        }
        int i=0;
        int n=colors.size();
        while(i<n)
        {
            int count=1;
            bool flag=true;
            int j=i+1;
            int last=temp[i];
            while(j<temp.size())
            {
                if(temp[j]==last)
                {
                    i=j;
                    break;
                }
                else{
                    last=temp[j];
                    count++;
                }
                if(count==k)
                {
                    ans++;
                    j++;
                    break;
                }
                j++;
            }
            if(i!=j)
            {
                while(j<temp.size() && j<(n+k-1))
                {
                    if(temp[j]!=temp[j-1])
                    {
                        ans++;
                        i++;
                        j++;
                    }
                    else{
                        i=j;
                        break;
                    }
                }
                // cout<<i<<" "<<j<<endl;
                if(j==n+k-1)
                {
                    return ans;
                }
            }
        }
        return ans;
    }
};
