class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        vector<int>nv(n+k-1,0);
        for(int i=0;i<n;i++) nv[i] = colors[i];
        for(int i=n;i<n+k-1;i++)nv[i] = colors[i%n];

        set<int>problem;
        for(int i=0;i+2<n+k;i++){
            if(nv[i] == nv[i+1])problem.insert(i);
        }
        for(int i=0;i<n;i++){
            int start = i;
            int end = i + k - 1;
            auto it = problem.lower_bound(start);
            if(it == problem.end()){
                // ok 
                ans ++;
            }
            else{
                int ind = *it;
                if(ind >= end){
                    ans ++;
                }
                else{
                    ans += 0;
                }
            }
        }
        return ans;
    }
};
