function numberOfAlternatingGroups(colors: number[], k: number): number {
    const N = colors.length;
    let count = 0;

    if (k === 1) {
        return N;
    }

    let alternatingLength = 1;
    let i = 1;
    while (i < N + k - 1) {
        const current = i % N;
        const previous = (i - 1) % N;

        if (colors[current] !== colors[previous]) {
            alternatingLength++;
        } else {
            alternatingLength = 1;
        }

        if (alternatingLength >= k) {
            count++;
        }

        i++;
    }

    return count;
}
