class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &a, int k)
    {
        int n = size(a), ans = 0, c = 0, i = 0;
        while (i + 1 < k) if (a[i] != a[++i]) c++;
        ans += (k == c + 1);

        for(int i = k; i < n + k - 1; i++)
        {
            c -= (a[(i - k) % n] != a[(i - k + 1) % n]);
            c += (a[(i - 1) % n] != a[i % n]);
            ans += (k == c + 1);
        }
        return ans;
    }
};
