class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v = {0, 0};
        for(int i = 0; i < 2; i++)
            for(auto &val:colors)
                v.push_back(val);
        int n = v.size(), N = colors.size();
        vector<int> pre(n);
        for(int i = 2; i < n; i++)
            pre[i] = v[i] + pre[i-2];
        for(int i = 0; i < 5; i++)
            pre.push_back(pre[pre.size()-2]);
        int ans = 0;
        // for(int i = 0; i < n; i++)
        //     cout << pre[i] << " ";
        // cout << "\n";
        int up = (k-1)/2;
        for(int i = 2; i < N+2; i++){
            // cout << i << " : ";
            if(v[i] == 0){
                int ind = i+2*up;
                int z = pre[ind] - pre[i-2];
                if(ind+1 <= i+k-1)
                    ++ind;
                else
                    --ind;
                int o = pre[ind] - pre[i-1];
                if(o == k/2 && z == 0)
                    ans++;
                // cout << z << " " << o;

            }else{
                int ind = i+2*up;
                int o = pre[ind] - pre[i-2];
                // cout << "f: " << ind << " s: ";
                if(ind+1 <= i+k-1)
                    ++ind;
                else
                    --ind;
                int z = pre[ind] - pre[i-1];
                // cout << ind << "- ";
                if(o == (k+1)/2 && z == 0)
                    ans++;
                    // cout << z << " " << o;
            }
            // cout << "\n";
        }
        return ans;
    }
};
