class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int no=0;

        int x=-1;
        int n=a.size();

        for(int i=0;i<n;i++){
            if(a[i]==a[(i+1)%n])x=i;
        }
        if(x==-1)return n;
        vector<int> dp(n,0);
        dp[x]=1;
        for(int i = ((x-1+n)%n);i!=x;i=((i-1+n)%n)){
            if(a[i]==a[(i+1)%n])dp[i]=1;
            else dp[i]=dp[(i+1)%n]+1;
        }
        int c=0;
        for(int i=0;i<n;i++){
            if(dp[i]>=k)c++;
        }
        return c;
    }
};
