class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i=0, j=1;
        int ans = 0, n = colors.size();
        while(i<n){
            if(colors[j]!=colors[(j-1+n)%n]){
                if((j-i+n)%n==k-1){
                    i++, ans++;
                }
            } else {
                if(j<i) break;
                i = j;
            }
            j = (j+1)%n;
        }
        return ans;
    }
};
