class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int ans = 0;
        int ptr = k-3;
        int contAlt = 1;
        boolean start = false;

        while(!(ptr == 0 && start==true)){
            if(start == false && ptr==0)
                start = true;

            if(ptr<0)
                ptr+=n;

            if(colors[ptr] != colors[(ptr+1)%n]){
                contAlt++;
            }else{
                contAlt = 1;
            }
            if(contAlt >= k)
                ans++;

            // System.out.println(ptr+" "+contAlt);

            ptr--;
        }
        boolean flag = true;
        for(int i=1;i<k;i++){
            if(colors[i]==colors[i-1]){
                flag = false;
                break;
            }
        } 

        if(flag)
            ans+=1;

        return ans;
    }
}
