class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size(), ans = 0;

        int i = 0, j = 1;
        vector<int> v(n, 1), u(n, 1);
        for (int i = 1; i < n; i++) {
            if (a[i] == a[i - 1]) j = 1;
            else j++;
            v[i] = j;
            if (v[i] >= k) ans++;
        }

        if (a[0] == a[n - 1]) return ans;

        j = 1;
        for (int i = (n - 2); i >= 0; i--) {
            if (a[i] == a[i + 1]) j = 1;
            else j++;
            u[i] = j;
        }

        i = 0, j = (n - k + 1);
        while (i < (k - 1)) {
            if ((v[i] + u[j]) >= k) ans++;
            i++;
            j++;
        }

        return ans;
    }
};
