class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int prev = -1;
        int res = 0;
        for (int i = 0; i < n + k - 2; i++) {
            if (colors[i % n] == colors[(i + 1) % n]) prev = i;
            else {
                if (i >= k - 2 && i >= prev + k - 1) res++;
            }
        }
        return res;
    }
}
