class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ct = 0, ans = 0;
        int j = 0;
        while(j < n && colors[j] != colors[(j+1)%n])
            ++j;
        if(j == n)
            return n;
        for(int i=j; i<j+n; ++i)
        {
            if(colors[(i+1)%n] != colors[i%n])
                ++ct;
            else
            {
                ans += max(ct-k+2,0);
                ct = 0;
            }
        }
        ans += max(ct+2-k,0);
        return ans;
    }
};
