class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        n = colors.size();
        int ret = 0;
        int cur = 1;
        bool going = false;
        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                cur++;
                cur = min(cur, k);
            }
            else{
                cur = 1;
                going = false;
            }

            if(cur == k){
                going = true;
            }
            if(going){ret++;}
        }
        return(ret);
    }
};
