class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
      for(int i=0; i<k-1; i++){
         a.push_back(a[i]);
      }   
      int n = a.size();

      // vector<int> pref(n+1,0);
      // for(int i=0; i<n; i++){
      //    pref[i+1]= pref[i] + a[i];
      // }

      int e=0,o=0;
      for(int i=0; i<k-1; i++){
         if(i%2==0) e+=a[i];
         else o+=a[i];   
      }

      int ans=0;
      int l = 0,r =k-1;
      while(r<n){
         if(r%2==0) e+=a[r];
         else o+=a[r]; 

         if(k%2==0){
            if(o==0 && e==k/2) ans++;
            if(e==0 && o==k/2) ans++;
         }
         else{
            if(r%2!=0){
                if(o==0 && e==k/2) ans++;
                if(e==0 &&(o==(k+1)/2)) ans++;  
            } 
            else{
                if(e==0 && o==k/2) ans++;
                if(o==0 &&(e==(k+1)/2)) ans++;   
            } 
         }

         if(l%2==0) e-=a[l];
         else o-=a[l]; 
         l++;r++;
      }
      return ans;
    }
};
