class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& tiles, int groupLength ) {
        int totalTiles = tiles.size();
    int groupCount = 0;

    if (groupLength == 1) {
        return totalTiles;
    }

    int altLength = 1;
    for (int idx = 1; idx < totalTiles + groupLength - 1; ++idx) {
        int curIdx = idx % totalTiles;
        int prevIdx = (idx - 1) % totalTiles;

        if (tiles[curIdx] != tiles[prevIdx]) {
            ++altLength;
        } else {
            altLength = 1;
        }

        if (altLength >= groupLength) {
            ++groupCount;
        }
    }

    return groupCount;
    }
};
