class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        int n=colors.size();

        vector<int>v;

        for(int x: colors){
            v.push_back(x);
        }

         for(int x: colors){
            v.push_back(x);
        }

        int i=0, j=0;

        int ans=0;

        while(i<2*n && j<n){

            if(i-j+1==k){
                ans++;
                j++;
            }

            if(i+1<2*n && v[i]!=v[i+1]){
                i++;
            }
            else{
                i++;
                j=i;
            }

            // cout<<i<<" "<<j<<endl;

        }

        return ans;
    }
};
