class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        c.insert(c.end(),c.begin(),c.begin()+k-1);
        int ans=0,temp=1;
        for(int i=1;i<c.size();i++){
            temp=(c[i]!=c[i-1])? temp+1:1;
            if(temp>=k){
                ans++;
            }
        }
        return ans;
    }
};
