class Solution {
public:

    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size() ;
        vector<int> even(2 , 0) , odd(2 , 0) ;
        for(int i = 0 ; i < n ; ++ i)
        {
            a.push_back(a[i]) ;
            if ( i < k ) 
            {
                if ( i & 1 ) {
                    odd[a[i]] ++ ; 
                } else {
                    even[a[i]] ++ ;
                }
            }
        }

        int ans = check(even , odd) ;

        for(int i = 1 ; i < n ; ++ i)
        {
            if ( (i - 1) & 1 ) 
            {
                odd[a[i - 1]] -- ; 
            } else {
                even[a[i - 1]] -- ;
            }
            if ( (i + k - 1) & 1 )
            {
                odd[a[i + k - 1]] ++ ; 
            } else {
                even[a[i + k - 1]] ++ ;
            }
            ans += check(even , odd) ;
        }
        return ans ;
    }
    bool check(vector<int>even , vector<int> odd)
    {
        if ( even[0] > 0 ) {
            if ( even[1] > 0 || odd[0] > 0 ) return false ;
        } else {
            if ( odd[1] > 0 ) return false ;
        }
        return true; 
    }
};
