class Solution:
	def numberOfAlternatingGroups(self, nums: List[int], k: int) -> int:
		if k==1:  return len(nums)
		nums=nums*2
		n=len(nums)
		ans=0
		i,j=0,1
		while i<n//2:
			while j-i+1<k and nums[j]!=nums[j-1]:
				j+=1
			if j-i+1==k and nums[j]!=nums[j-1]:
				ans+=1
				i,j=i+1, j+1
			else:
				i,j=j,j+1
		return ans
