class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        for(int i = 0 ; i < k - 1 ; ++i) {
            colors.push_back(colors[i]);
        }
        N += k - 1;
        int last = -1;
        for(int i = 0 ; i < k - 1 ; ++i) {
            if(colors[i] == colors[i + 1]) {
                last = i;
            }
        }
        int ans = 0;
        for(int i = k - 1 ; i < N ; ++i) {
            if(i - k >= last)
                ++ans;
            if(i + 1 < N && colors[i] == colors[i + 1])
                last = i;
        }
        return ans;
    }
};
