class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        m = len(colors)
        colors += colors[:-1]
        n = len(colors)
        dp = [1] * n
        for i in range(1, n):
            if colors[i - 1] != colors[i]:
                dp[i] += dp[i - 1]
        ans = 0
        for i in range(k - 1, m + k - 1):
            ans += dp[i] >= k
        return ans
