class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int groupCount = 0;
        int start = 0;
        int end = 1;
        int n = colors.length;

        while (end < n + k && start < n) {
            int tempEndIndex = end;
            int tempEnd = tempEndIndex % n;


            while (colors[tempEnd] != colors[(tempEnd - 1 + n) % n] && tempEndIndex < n + k - 1) {
                tempEndIndex++;
                tempEnd = tempEndIndex % n;
            }


            groupCount += Math.max(tempEndIndex - start - k + 1, 0);
            start = tempEndIndex;
            end = start + 1;
        }

        return groupCount;
    }
}
