class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        long long ct = 0,ans = 1,i = 0;
        i++;
        while(i<(n+k-1)){ 
            long long curr=i,prev=i-1;
            if(curr >= n) curr-=n;
            if(prev >= n) prev-=n; 
            if(colors[prev]==colors[curr]) ans=1; 
            else ans++; 
            i++;
            if (ans>= k) ct++; 

        } 

        return ct; 
    } 
};
