class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        int n = color.size();
        vector<int> a(2*n+1),f(2*n+1);
        for(int i=1;i<=n;i++) a[i] = a[i + n] = color[i-1];
        f[2*n] = 1;
        for(int i=2*n-1;i>=1;i--){
            if(a[i] != a[i+1]) f[i] = f[i+1] + 1;
            else f[i] = 1;
        }
        int ans = 0;
        for(int i=1;i<=n;i++){
            if(f[i] >= k) ans++;
        }
        return ans;
    }
};
