class Solution {
public:
    struct DS{
        deque<int> dq;
        void insert(int i,vector<int> &C){
            int j = i-1;
            if(j<0)j+=C.size();
            if(C[j]!=C[i])return;
            dq.push_back(j);
        }
        void remove(int i){
            if(dq.front()==i)dq.pop_front();
        }
    };
    int numberOfAlternatingGroups(vector<int>& C, int k) {
        int ans = 0,n = C.size();
        DS d;
        for(int i=1;i<k-1;i++)d.insert(i,C);
        for(int i=0;i<n;i++){
            int j = (i+k-1)%n;
            d.insert(j,C);
            if(d.dq.empty())ans++;
            d.remove(i);
        }
        return ans;
    }
};
