class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        for(int i=0;i<n+k-1;++i)    a.push_back(a[i]);
        int ans=0,len=1;
        for(int i=1;i<n+k-1;++i)
        {
            if(a[i]!=a[i-1])    len++;
            else    len=1;
            if(i>=k-1&&len>=k)    ans++;
        }
        return ans;
    }
};
