class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n=colors.size(); 
        int ans=0; 

        if (k==1) 
            return n; 


        int temp=1; 
        int i=1; 
        while (i<n+k-1) { 
            int current =i; 
            int previous =i-1; 

            if (current>=n) 
                current-=n; 

            if (previous>=n)  
                previous-=n; 


            if (colors[current]!=colors[previous]) 
                temp++; 
            else 
                temp=1; 


            if (temp>=k) 
                ++ans; 


            ++i; 
        } 
        return ans; 
    } 
};
