class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        c = c;
        vector<int> a = c;
        for(auto& d: a) c.push_back(d); 
        vector<int> dp(2 * n);
        for(int i = 2 * n - 2; i >= 0; i--) {
            if(c[i] != c[i + 1]) dp[i] = dp[i + 1] + 1;
            else dp[i] = 1;
        }
        int t = 0;
        for(int i = 0; i < n; i++) {
            if(dp[i] >= k) t++;
        }
        return t;
    }
};
