class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        n = len(colors)
        count = 0

        alternating_len = 1 

        for i in range(1, n + k - 1):
            curr = i % n 
            prev = (i - 1) % n 

            if colors[curr] != colors[prev]:
                alternating_len += 1 

            else:
                alternating_len = 1

            if alternating_len >= k:
                count += 1 

        return count
