var numberOfAlternatingGroups = function(colors, k) {
    let n = colors.length;
    let ans = 0;
    const calculateAlternatingCount = (colors, start, end) => {
        let count = 0;
        for (let i = start; i < end; ++i) {
            if (colors[i] !== colors[i + 1]) {
                count++;
            }
        }
        return count;
    };
    let initialCount = calculateAlternatingCount(colors, 0, k - 1);
    if (initialCount === k - 1) {
        ans++;
    }
    for (let i = k; i < n + k - 1; ++i) {
        if (colors[(i - 1) % n] !== colors[i % n]) {
            initialCount++;
        }
        if (colors[(i - k) % n] !== colors[(i - k + 1) % n]) {
            initialCount--;
        }
        if (initialCount === k - 1) {
            ans++;
        }
    }

    return ans;
};
