class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> ns(colors.begin(), colors.end());
        for(auto &v:colors) ns.push_back(v);
        int n=ns.size()/2;
        set<int> st;
        for(int i=1; i<n+k; i++){
            if((ns[i]^ns[i-1])==0){
                st.insert(i);
            }
        }
        int res=0;
        for(int i=0; i<n; i++){
            int j=i+k-1;
            auto it=st.lower_bound(i+1);
            if(it==st.end() || *it>j){
                ++res;
            }
        }
        return res;
    }
};
