class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int cnt=0;int a=1;
        int n=c.size();
        for(int b=1;b<c.size()+k;b++){
            int i=b;
            if(i>=n)i%=n;
            if(a==k){
                cnt++;
                a--;
            }
            int j=i-1;
            if(i==0)j=c.size()-1;
            if(c[i]!=c[j])a++;
            else a=1;
        }
        return cnt;
    }
};
