class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n, cnt = len(colors), 0
        diff = [(colors[i] != colors[(i + 1) % n]) for i in range(n)]
        # print(diff)
        valid = sum(diff[:k-1])
        for i in range(n):
            if valid == k - 1:
                cnt += 1
            valid -= diff[i]  
            valid += diff[(i+k-1) % n]

        return cnt
