class Solution(object):
    def numberOfAlternatingGroups(self, col, k):
        S = len(col)
        cnt = 0
        if k == 1:
            return S
        alt_len = 1
        i = 1
        while i < S + k - 1:
            curr = i
            prev = i - 1
            if curr >= S:
                curr -= S
            if prev >= S:
                prev -= S
            if col[curr] != col[prev]:
                alt_len += 1
            else:
                alt_len = 1
            if alt_len >= k:
                cnt += 1
            i += 1
        return cnt
