class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size(),ans=0;

        int cnt=1;
        for(int i=1;i<n+k-1;i++){
            if(a[i%n]!=a[(i+n-1)%n]){
                cnt++;
            }else{
                cnt=1;
            }
            if(cnt>=k) ans++;
        }

        return ans;
    }
};
