class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        set <int> s;
        int n = colors.size();
        for(int i = 0 ; i < k-1 ; i++){
            if(colors[i]==colors[i+1]){
                s.insert(i);
            }
        }
        int ans = 0;
        if(s.size()==0)ans++;
        for(int i = 1 ; i < n ; i++){
            if(s.find(i-1)!=s.end()){
                s.erase(i-1);
            }
            if(colors[(i+k-2)%n]==colors[(i+k-1)%n]){
                s.insert((i+k-2)%n);
            }
            if(s.size()==0)ans++;
        }
        return ans;
    }
};
