class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0, i, n=colors.size(), c, j;

        int pre[n];

        if(colors[0]==colors[n-1]){
            pre[0]=1;
        }else{
            pre[0]=0;
        }

        for(i=1; i<n; i++)
        {
            if(colors[i]!=colors[i-1]){
                pre[i]=0;
            }else{
                pre[i]=1;
            }
        }

        for(i=1; i<n; i++)
        {
            pre[i]+=pre[i-1];
        }

        for(i=0; i<n; i++)
        {
            if(i+k-1>=n){
                c=pre[n-1]+pre[(i+k-1)%n]-pre[i];
            }else{
                c=pre[i+k-1]-pre[i];
            }

            if(c==0){
                ans++;
            }

            //cout<<c<<" ";
        }

        return ans;
    }
};
