class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k - 1; i++) colors.push_back(colors[i]);
        int c = 0, ans = 0;
        for (int i = 1; i < k; i++) {
            if (colors[i] == colors[i - 1]) c++;
        }
        if (c == 0) ans++;
        for (int i = k; i < colors.size(); i++) {
            if (colors[i] == colors[i - 1]) c++;
            if (colors[i - k] == colors[i - k + 1]) c--;
            if (c == 0) ans++;
        }
        return ans;
    }
};
