#include <vector>
#include <iostream>

using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k) {
        if (k == 1) {
            return colors.size();
        }
        return countAlternatingGroups(colors, k);
    }

private:
    int countAlternatingGroups(const vector<int> &colors, int k) {
        int n = colors.size();
        int count = 0;
        int len = 1;

        for (int i = 1; i < n + k - 1; ++i) {
            int cur = i % n;
            int prev = (i - 1) % n;

            if (colors[cur] != colors[prev]) {
                ++len;
            } else {
                len = 1;
            }

            if (len >= k) {
                ++count;
            }
        }

        return count;
    }
};
