class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int start = 0;
        int consecutive = 1;
        int cnt = 0;
        int i = 1;
        while (start < n){
            if (colors[i % n] != colors[(i - 1 + n) % n]){
                if (consecutive < k)
                    consecutive++;
                    // if (consecutive == k){
                    //     cnt++;
                    //     cout << start << " " << i << '\n';
                    // }
                if (consecutive == k){
                    cnt++;
                    start++;
                }

                // else {
                //     cnt++;
                //     cout << start << " " << i << '\n';
                //     start++;
                // }

            }
            else {
                start = i;
                consecutive = 1;
            }
            i++;
        }
        return cnt;
    }
};
