class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int j = 0;
        int last = colors[0];
        int count = 0;
        for (int i = 1; i < n+k-1; i++) {
            int index = i % n;
            if(colors[index] == last) {
                j = index;
            } else {
                if (j + k - 1 == i) {
                    count++;
                    j++;
                }
            }
            last = colors[index];
        }
        return count;
    }
}
