class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),pre=-1;
        int l=0,res=0;
        while(l<n){
            int ptr=l;
            while(ptr+1<n+k-1&&colors[ptr%n]!=colors[(ptr+1)%n]){
                ptr++;
            }
            res+=max(0,ptr-l+2-k);
            l=ptr+1;
        }
        return res;
    }
};
