class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size = colors.size();  
        int rs = 0; 
        if (k == 1) return size;  
        int len = 1;  
        int ind = 1;  
        while (ind < size + k - 1) {  
            int n = ind;  
            int m = ind - 1;  
            if (m >= size) m = m - size;  
            if (n >= size) n = n - size;  
            if (colors[n] != colors[m]) len++;  
            else len = 1;
            if (len >= k) rs++;  
            ind++;  
        }
        return rs;  
    }
};
