class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        for(int i=0;i<k;i++) arr.push_back(arr[i]);
        int N = arr.size(),Ans = 0;
        vector<int> prefix(N+1,0);
        k--;
        for(int i=1;i<N;i++) prefix[i] += prefix[i-1] + abs(arr[i]-arr[i-1]);
        for(int i=k+1;i<N;i++) if(prefix[i]-prefix[i-k] == k) Ans++;
        return Ans;
    }
};
