class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        for(int i=0;i<k-1;i++){
            c.push_back(c[i]);

        }

        vector<int> p;
        p.push_back(1);
        for(int i=1;i<c.size();i++){
            if(c[i]!=c[i-1]){
                p.push_back(p[i-1]+1);
            }
            else{
                p.push_back(1);
            }
        }
        int ans =0;
        for(int i=0;i<n;i++){
            if(p[i+k-1]-p[i]==k-1)ans++;

        }



        return ans;
    }
};
