class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int m=arr.size();

        for(int i=0;i<k-1;i++)arr.pb(arr[i]);
        int n=arr.size();
        int res=0;
        for(int i=0;i<=n-k;){
            int j=i+1;
            while(j<n){
                if(arr[j]!=arr[j-1]){
                    if(j-i+1>=k)res++;
                    j++;
                }
                else{
                    i=j;
                    break;
                }
            }
            if(j==n)break;
        }


        return res;


    }
};
