class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {

        for(int i=0;i<k-1;i++)
        {
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        vector<int>v(n,-1);

        v[0]=1;
        int cnt=2;
        for(int i=1;i<n;i++)
        {
            if(colors[i]!=colors[i-1])
                v[i]=v[i-1];
            else
                v[i]=cnt++;

        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            if(i+k-1<n && v[i]==v[i+k-1])
            {
                ans++;
            }
        }
        // for(auto it:v)
        //     cout<<it<<" ";

        return ans;
    }
};
