class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int i=0,j=0;
        int ans=0;
        while(i<n){
            if(colors[j%n] != colors[(j+1)%n]){
                j++;
            }
            else{
                j++;
                i=j;
            }
            if(j-i+1 == k){
                ans++;
                i++;
            }
        }
        return ans;
    }
};
