mt19937 rng(random_device{}());
int rnd(int mod) { return rng() % mod; }

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        vector<int> b(2 * n);
        for (int i = 1; i < 2 * n; i++) {
            int cur = i % n, pre = (i - 1 + n) % n;
            b[i] = a[cur] != a[pre];
        }
        for (int i = 1; i < 2 * n; i++) {
            b[i] += b[i - 1];
        }
        int ans = 0;
        for (int l = 0; l < n; l++) {
            int r = l + k - 1;
            int t = b[r] - b[l];
            if (t == k - 1) ans++;
        }
        return ans;
    }
};

#ifdef RUN
int main() {
    ios_base::sync_with_stdio(false);
    stringstream out_stream;

    vector<int> colors;
    LeetCodeIO::scan(cin, colors);
    int k;
    LeetCodeIO::scan(cin, k);

    Solution *obj = new Solution();
    auto res = obj->numberOfAlternatingGroups(colors, k);
    LeetCodeIO::print(out_stream, res);
    cout << "\noutput: " << out_stream.rdbuf() << endl;

    delete obj;
    return 0;
}
#endif
